import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components'
import { publicRequest } from '../requestMethods';


const Container=styled.div`
display:flex;
padding:20px;
flex-wrap: wrap;
justify-content:space-between;`;
const Img=styled.img`
height:8rem;
`;

const Rooms = ({cat,filters,sort}) => {
    console.log(cat,filters,sort)
    const [products,setProducts]=useState([])
    const [filteredProducts,setFilteredProducts]=useState([])
    useEffect(()=>{
         const getProducts = async ()=>{
            try{
                const res=await publicRequest.get(`/rooms/double`)
              // const res= await axios.get(cat?`http://localhost:8181/api/products?category=${cat}`:`http://localhost:8181/api/products`)
               console.log(res)
               setProducts(res.data)
            }catch(err){

            }
        }
        getProducts()
    },[cat])
    useEffect(()=>{
   cat && setFilteredProducts(products.filter((item)=>Object.entries(filters).every(([key,value])=>item[key].includes(value))))
    },[products,cat,filters])

    useEffect(()=>{
        if(sort==="newest"){
            setFilteredProducts(prev=>
                [...prev].sort((a, b) => a.createdAt - b.createdAt)
                )
        }else if (sort === "asc") {
            setFilteredProducts((prev) =>
              [...prev].sort((a, b) => a.price - b.price)
            );
          } else {
            setFilteredProducts((prev) =>
              [...prev].sort((a, b) => b.price - a.price)
            );
          }
    },[sort])
    return (
        <Container>

            <section>

        <div>
          <Img src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?cs=srgb&dl=pexels-jean-van-der-meulen-1457842.jpg&fm=jpg" alt="a"/>
          <h6>${20}</h6>
          <p>per night</p>
        </div>
        <Link to={`/id`} className="btn-primary">
          features
        </Link>

      <p>"room info"</p>
    </section>

    <section>

        <div>
          <Img src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?cs=srgb&dl=pexels-jean-van-der-meulen-1457842.jpg&fm=jpg" alt="a"/>
          <h6>${20}</h6>
          <p>per night</p>
        </div>
        <Link to={`/id`} className="btn-primary">
          features
        </Link>

      <p>"room info"</p>
    </section>

    <section>

        <div>
          <Img src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?cs=srgb&dl=pexels-jean-van-der-meulen-1457842.jpg&fm=jpg" alt="a"/>
          <h6>${20}</h6>
          <p>per night</p>
        </div>
        <Link to={`/id`} className="btn-primary">
          features
        </Link>

      <p>"room info"</p>
    </section>
    <section>

        <div>
          <Img src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?cs=srgb&dl=pexels-jean-van-der-meulen-1457842.jpg&fm=jpg" alt="a"/>
          <h6>${20}</h6>
          <p>per night</p>
        </div>
        <Link to={`/id`} className="btn-primary">
          features
        </Link>

      <p>"room info"</p>
    </section>
        </Container>
    )
}

export default Rooms





