import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useLocation,Link,useHistory} from 'react-router-dom';
import styled from 'styled-components'


import { publicRequest } from '../requestMethods';
import Cart from './Cart';
import Food from './Food';


const Container=styled.div`
display:flex;`


const Wrapper=styled.div`
padding:50px;
flex:3;
display:flex;`
const WrapperF=styled.div`
padding:50px;
flex:1;`
const WrapperC=styled.div`
padding:50px;
flex:1;`



const ImgContainer=styled.div`
flex:1;
`


const Image=styled.img`
width:100%;
height:80vh;
object-fit:cover;`;


const InfoContainer=styled.div`
flex:1;
padding:0px 50px;
`


const Title=styled.h1`
font-weight:200`;

const Desc=styled.p`
margin:20px 0px;`;

const Price=styled.span`
font-weight: 100;
  font-size: 40px;`;

  const FilterContainer=styled.div`
  width:50%;
  margin:30px 0px;
  display:flex;
  justify-Content:space-between;`



  const Filter=styled.div`
  display:flex;
  align-items:center;`;

  const FilterText=styled.h1``;

  const FilterColor=styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
  margin: 0px 5px;
  cursor: pointer;`;

  const FilterSize=styled.select`
  margin-left:10px;
  padding:5px;`;

  const FilterSizeOption=styled.option``;

  const FilterTitle=styled.span`
  font-size:20px;
  font-weight:200;`;

  const AddContainer=styled.div`
  width:50%;
  display:flex;
  align-items:center;
  justify-content:space-between;
  `;

  const AmountContainer=styled.div`
  display:flex;
  align-items:center;
  font-weight:700;`;



  const Amount=styled.div`
  width:30px;
  height:30px;
  border-radius:10px;
  border: 1px solid teal;
  display:flex;
  align-items:center;
  justify-content:center;
  margin:0px 5px;
`;



  const Button=styled.button`
  padding:15px;
  border: 1px solid teal;
  order: 2px solid teal;
  background-color: white;
  cursor: pointer;
  font-weight: 500;
  &:hover{
      background-color:#f8f4f4;
  }
  `

const Guest = () => {
  const [price,setPrice]=useState(0)
  const [filters,setFilter]=useState({})
  const [showfood,setshowfood]=useState(false)
  const [fp,setFP]=useState(0)
  const location=useLocation()
  const id=location.pathname.split("/")[2]
   console.log(id)

  const [product,setProduct]=useState({})

  const [quantity, setQuantity] = useState(1);
  useEffect(()=>{
    const getProducts= async ()=>{
      try{
        const res = await publicRequest.get("user/"+id)
        console.log(res.data.message)
        setProduct(res.data.message)
        //console.log(res.data)

      }catch(err){

      }
    }
    getProducts()
  },[id])
  const onChangeValue=(event)=> {
    console.log(event.target.value);
    if(event.target.value==='yes')
    setshowfood(true)
    else
    setshowfood(false)
  }
  const foodoption=(event)=>{
    setFP(event.target.value)
    console.log(fp)
  }
  const handleFilters=(e)=>{

    const value=e.target.value;

    setPrice(parseInt(value))
    var index = e.nativeEvent.target.selectedIndex;
    var text =e.nativeEvent.target[index].text;
    console.log(text);
    setFilter({...filters,
      [e.target.name]:value})
      console.log(filters)
      console.log(price)

    }


    return (
      <Container>

      <Wrapper>
      <ImgContainer>
    <Image src={product.room_img}/>
  </ImgContainer>
  <InfoContainer>
    <Title>{product.room_title}</Title>
    <Desc>
      {"is very nice room"}
    </Desc>
    <Price>$ {product.room_price}</Price>
    <h2>need food?</h2>
    <div onChange={onChangeValue}>
        <input type="radio" value="yes" name="gender" /> Yes
        <input type="radio" value="no" name="gender" /> No

      </div>
      <div className="date">

     <h3>select date for book <input type="date"/></h3>
      </div>
      {showfood && <div className="food">
        <div>
          <h3>Breakfast</h3>
      <select name="breakfast" onChange={handleFilters}>
            <option disabled >
              select food
            </option>
            <option value="10">biriyani-100</option>
            <option value="5" >black cofee</option>
            <option value="2">dosha</option>
            <option value="3">puttu</option>

          </select>
          </div>
          <div>
            <h3>Lunch</h3>
          <select name="lunch" onChange={handleFilters}>
            <option disabled >
              select food
            </option>
            <option value="10">biriyani-100</option>
            <option value="5" >black cofee</option>
            <option value="2">dosha</option>
            <option value="3">puttu</option>

          </select>
          </div>
          <div>
            <h3>Dinner</h3>
          <select name="dinner" onChange={handleFilters}>
            <option disabled >
              select food
            </option>
            <option value="10">biriyani-100</option>
            <option value="5" >black cofee</option>
            <option value="2">dosha</option>
            <option value="3">puttu</option>

          </select>
          </div>


          </div>
          }

    </InfoContainer>

    </Wrapper>
    <WrapperC>
      <Cart title={product.room_title} price={product.room_price} roomId={product.room_id} fdprice={fp}/>
    </WrapperC>

    </Container>
    )
}

export default Guest
