    let value=2;
    const [message,setMessage]=useState("")
    let p=props.fdprice+props.price
    console.log(p)
    console.log(props)
    if(props.nofd==="NaN"||"undefined"){
        value=1
    }else{
        value=props.nofd
    }

  const submithandler=()=>{
      useEffect(()=>{
          const result= publicRequest.post("/user/book",{email:"n@gmail.com",roomId:3})
         setMessage(result.data.message)
      },[])
  }

    return (
        <div className="cart">
           <h5>room no:{props.roomId}</h5>
           <h5>Type:single</h5>
           <h4>Total:{(p*(props.nofd))||p}</h4>
           <h4>date:{props.sdate}-{"  "}To{"  "}-{props.edate}</h4>
           <h4>total days:{props.nofd||1}</h4>
           <button onClick={submithandler}>Book</button>
            {message}
        </div>

    )
}

export default Cart
