import {createSlice} from "@reduxjs/toolkit" 

const CartSlice=createSlice({
    name:"cart",
    initialState:{
        rooms:[],
        days:0,
        total:0,
    },
    reducers:{
        addRoom:(state,action)=>{

            state.rooms=[]//.push(action.payload.room)

            state.days=0//action.payload.days

            state.total=0//state.total+action.payload.total

        }
    }             
    })
export const {addRoom}=CartSlice.actions
export default CartSlice.reducer;
