import { setUseProxies } from '@reduxjs/toolkit/node_modules/immer'
import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import Banner from '../Components/Banner'
import Hero from '../Components/Hero'
import { publicRequest } from '../requestMethods'
import Button from '../UI/Button/Buttton'

const Register = () => {
    const [message,setMessage]=useState()
    const [name,setName]=useState()
    const [email,setEmail]=useState()
    const [password,setPassword]=useState()
    const dispatch=useDispatch()
    const [user,setUser]=useState(null)
    const [emailIsValid, setEmailIsValid] = useState(false);
    const [passwordIsValid, setPasswordIsValid] = useState(false);
    const [formIsValid, setFormIsValid] = useState(false);

    useEffect(()=>{
      const identifier= setTimeout(()=>{
         console.log("checking form validity")
       setFormIsValid(
         emailIsValid && passwordIsValid
       );
       },500)
       return ()=>{
         console.log("Clean Up")
         console.log(formIsValid)
       clearTimeout(identifier)
       }

     },[formIsValid])

    const handleClick=(event)=>{
     event.preventDefault();
     if(formIsValid===true){
      setUser({name,email,password})
     }
     setUser(null)

    }
    useEffect(()=>{
        const Res= async ()=>{
          try{
            const res = await publicRequest.post("user/sign-up",{name,email,password})
            console.log(res.data.message)
            setMessage(res.data.message)
            //console.log(res.data)

          }catch(err){

          }
        }
        Res()
      },[user])

      const validateEmailHandler=()=>{
        setEmailIsValid(email.includes('@'));
      }
      const validatePasswordHandler=()=>{
        setPasswordIsValid(password.length > 2);
        console.log(passwordIsValid)
      }
      const passwordSet=(event)=>{
        setPassword(event.target.value)

      }
      const emailSet=(event)=>{
        setEmail(event.target.value)

      }

    return (
        <Hero>
            <Banner
          title="Register"
          subtitle="Create Your Account"
        >
            <form onSubmit={handleClick}>
            <label>Name</label>
            <input type="text" onChange={(e)=>setName(e.target.value)} />
            <label htmlFor="email">Email</label>
            <input type="email" id="email" value={email} onChange={emailSet} onBlur={validateEmailHandler} />

            <label htmlFor="password">Password</label>

            <input type="password" id="password" value={password} onChange={passwordSet} onBlur={validatePasswordHandler}/>

            <button type="submit" disabled={!formIsValid}>SUBMIT</button>
            </form>
            {message &&  <section>{message}</section>}



        </Banner>

        </Hero>
    )
}

export default Register
